const posts =async()=>{       
    const response = await fetch('https://jsonplaceholder.typicode.com/posts');
    const data = await response.json();  
    return data;
}

const post = async(idPost)=>{
    const response = await fetch(`https://jsonplaceholder.typicode.com/posts/${idPost}`);
    const data = await response.json();  
    return data
}

export {posts, post}
