      arrayCategories.value = await categories();
    });
    console.log("appvue ", arrayCategories);
    const __returned__ = {
      couleursCategorie,
      drawerOpened,
      arrayCategories,
      onMounted,
      ref,
      get RouterView() {
        return RouterView;
      },
      AppHeader,
      get categories() {
        return categories;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
