const posts =()=>{       
    const arrayPosts=[];
    const fetchPosts = async () =>{
        const response = await fetch('https://jsonplaceholder.typicode.com/posts');
        const data = await response.json();  
        
        arrayPosts.value=data;
    };
    fetchPosts()
    return arrayPosts;
}
const post = (idPost)=>{
    const response =  fetch(`https://jsonplaceholder.typicode.com/posts/${idPost}`);
    const data =  response.json();  
    return data
}

export {posts, post}
